and A.COMN_GRP_CD = #{comnGrpCd}
order by nvl(A.SCRN_DSPL_SQNC, 0), A.COMN_DTLS_CD

/*SQL_ID: com-sds-healthcare-ehr-com-cc-ccc-dao-CccComcodeDAO-listComcode3 */ select /*+ ccd_comcode_l12$S01_사용자명_코드상세 조회 */ A.COMN_DTLS_CD /* 공통세부코드 */ , nvl(B.DETL_CD_NM, A.DETL_CD_NM) DETL_CD_NM /* 상세코드명 */ , substrb(A.COMN_DTLS_CD || ' - ' || nvl(B.DETL_CD_NM, A.DETL_CD_NM), 1, 200) as DETL_CD_DSCR_CTN from CCCMCDDMT A /* 코드상세 */ , CCCMCDDLT B /* 언어별상세코드명 */ , CCCMCDMMT c where a.COMN_GRP_CD = c.COMN_GRP_CD and c.COMN_GRP_CD = #{comnGrpCd}
and trunc(c.APST_YMD) <= trunc(sysdate) and trunc(c.APFN_YMD) >= trunc(sysdate)
and A.COMN_GRP_CD = B.COMN_GRP_CD(+) and A.COMN_DTLS_CD = B.COMN_DTLS_CD(+) and ( (#{afiSrchCndtCtn} = '1') or ((#{afiSrchCndtCtn} = '2') and (A.COMN_DTLS_CD like #{comnDtlsCd} || '%')) or ((#{afiSrchCndtCtn} = '3') and (A.COMN_DTLS_CD = #{comnDtlsCd} )) ) and B.LNGG_CD(+) = #{lnggCd}
