/*SQL_ID: com-sds-healthcare-ehr-com-cc-ccc-dao-CccComcodeDAO-listComnDetlCd */
SELECT X.COMN_GRP_CD , X.COMN_DTLS_CD , X.DETL_CD_NM , X.DETL_CD_DSCR_CTN , X.APST_YMD , X.APFN_YMD , X.SCRN_DSPL_SQNC , X.FRST_RGSR_ID /* , X.FRST_RGSR_ID || ' [ ' || ( SELECT USER_NM FROM CCUSERAMT WHERE LGIN_ID = X.FRST_RGSR_ID AND ROWNUM < 2 ) || ' ]' AS USER_NM */ , DECODE( ( SELECT USER_NM FROM CCUSERAMT WHERE LGIN_ID = X.FRST_RGSR_ID AND ROWNUM < 2 ), '', X.FRST_RGSR_ID, ( X.FRST_RGSR_ID || ' [ ' || ( SELECT USER_NM FROM CCUSERAMT WHERE LGIN_ID = X.FRST_RGSR_ID AND ROWNUM < 2 ) || ' ]' )) as USER_NM , X.FRST_RGST_DT , X.LAST_UPDR_ID /* , X.LAST_UPDR_ID || ' [ ' || ( SELECT USER_NM FROM CCUSERAMT WHERE LGIN_ID = X.LAST_UPDR_ID AND ROWNUM < 2 ) || ' ]' AS USER_NM1 */ , DECODE( ( SELECT USER_NM FROM CCUSERAMT WHERE LGIN_ID = X.LAST_UPDR_ID AND ROWNUM < 2 ), '', X.LAST_UPDR_ID, ( X.LAST_UPDR_ID || ' [ ' || ( SELECT USER_NM FROM CCUSERAMT WHERE LGIN_ID = X.LAST_UPDR_ID AND ROWNUM < 2 ) || ' ]' )) as USER_NM1 , X.LAST_UPDT_DT FROM ( select /*+ ccd_comcode_l06$S01_사용자명_코드상세 조회 */ a.COMN_GRP_CD as COMN_GRP_CD /* 공통그룹코드 */ , a.COMN_DTLS_CD as COMN_DTLS_CD /* 공통세부코드 */ , nvl(b.DETL_CD_NM, a.DETL_CD_NM) as DETL_CD_NM , a.DETL_CD_DSCR_CTN as DETL_CD_DSCR_CTN /* 상세코드설명내용 */ , to_char(a.APST_YMD, 'YYYYMMDDHH24MISS') as APST_YMD /* 적용시작일자 */ , to_char(a.APFN_YMD, 'YYYYMMDDHH24MISS') as APFN_YMD /* 적용종료일자 */ , a.SCRN_DSPL_SQNC as SCRN_DSPL_SQNC /* 화면표시순서 */ , a.FRST_RGSR_ID as FRST_RGSR_ID /* 최초등록자ID */ , to_char(a.FRST_RGST_DT, 'YYYYMMDDHH24MISS') as FRST_RGST_DT /* 최초등록일시 */ , a.LAST_UPDR_ID as LAST_UPDR_ID /* 최종수정자ID */ , to_char(a.LAST_UPDT_DT, 'YYYYMMDDHH24MISS') as LAST_UPDT_DT /* 최종수정일시 */ from CCCMCDDMT a /* 공통코드값_T */ , CCCMCDDLT b /* 공통코드언어별코드값_T */ , CCCMCDMMT c /* 공통코드_T */ where c.COMN_GRP_CD = #{comnGrpCd} and trunc(c.APST_YMD) <= trunc(sysdate) and trunc(c.APFN_YMD) >= trunc(sysdate) and a.COMN_GRP_CD = c.COMN_GRP_CD and a.COMN_GRP_CD = b.COMN_GRP_CD(+) and a.COMN_DTLS_CD = b.COMN_DTLS_CD(+) and b.LNGG_CD(+) = #{gvLnggCd}
