and ( (a.COMN_DTLS_CD <> (select nvl(COMN_ENVR_STUP_GRP_DVSN_VL,'KOKR') from CCENSTGMT where COMN_ENVR_STUP_GRP_CD = 'LOCAL_LNG') ) and trunc(a.APST_YMD) <= trunc(sysdate) and trunc(a.APFN_YMD) >= trunc(sysdate) )
/* srchCndtCtn : 4 인 것은 [ CCC002F1 : 상세코드관리 ] 에서 사용한다. */ and ( (a.COMN_DTLS_CD like #{comnDtlsCd} || '%') or (a.DETL_CD_NM like '%' || #{detlCdNm} || '%') ) /* srchYn : Y : 삭제된 코드도 보기 */ and ( ( trunc( a.APST_YMD ) <= trunc( sysdate ) ) and ( trunc( a.APFN_YMD ) <= trunc( sysdate ) ) or ( trunc( a.APST_YMD ) <= trunc( sysdate ) ) and ( trunc( a.APFN_YMD ) > trunc( sysdate ) ) or ( trunc( a.APST_YMD ) <= trunc( sysdate ) ) and ( trunc( a.APFN_YMD ) <> trunc( to_date('29991231', 'YYYYMMDD') ) ) )
/* srchCndtCtn : 4 인 것은 [ CCC002F1 : 상세코드관리 ] 에서 사용한다. */ and ( (a.COMN_DTLS_CD like #{comnDtlsCd} || '%') or (a.DETL_CD_NM like '%' || #{detlCdNm} || '%') ) /* srchYn : N : 삭제 않된 코드만 보기 */ and ( ( trunc( a.APST_YMD ) <= trunc( sysdate ) ) and ( trunc( a.APFN_YMD ) > trunc( sysdate ) ) or ( trunc( a.APST_YMD ) <= trunc( sysdate ) ) and ( trunc( a.APFN_YMD ) >= trunc( to_date('29991231', 'YYYYMMDD') ) ) )
order by a.SCRN_DSPL_SQNC, a.COMN_DTLS_CD ) X

