/*SQL_ID: com-sds-healthcare-ehr-com-cc-ccc-dao-CccComcodeDAO-deleteComnCdItemVl */

	DELETE 	/*+ ccd_comcode_d04$I01_사용자명_코드항목값 삭제 */
	 FROM  	CCCMCDIDT                /*  공통코드추가항목값_T    */
   WHERE  	COMN_GRP_CD  = #{comnGrpCd}            /*  공통그룹코드  */
	   AND  	(	
					( #{srchCndtCtn} = '1' ) 
					or
	   				(
						/* srchCndtCtn : 2 : [ CCC002F1 : 상세코드관리 ] 화면에서 [ 삭제 ]기능에서 사용함. */ 
						( #{srchCndtCtn} = '2' ) and ( COMN_DTLS_CD = #{comnDtlsCd} ) 
					) 
					or
	   			    ( ( #{srchCndtCtn} = '3' ) and ( COMN_DTLS_CD = #{comnDtlsCd} ) and ( COMN_ITEM_CD = #{comnItemCd} ) ) 
					or
	   				(
						/* srchCndtCtn : 4 : [ CCC003F1 : 코드항목 관리 ] 화면에서 [ 삭제 ] 기능에서 사용함.  */ 
						( #{srchCndtCtn} = '4' ) and ( COMN_ITEM_CD = #{comnItemCd} ) 
					)
				)

/*SQL_ID: com-sds-healthcare-ehr-com-cc-ccc-dao-CccComcodeDAO-deleteComnDetlCdMltiLngg */

	DELETE		/*+ ccd_comcode_d06$I01_사용자명_언어별 상세코드명 삭제 */
	 FROM   	CCCMCDDLT	/* 공통코드언어별코드값_T */
   WHERE  		COMN_GRP_CD  = #{comnGrpCd}
       AND  		( 
						( #{srchCndtCtn} = '1' ) 
						or
	   					( 
							/* srchCndtCtn : 2 : [ CCC002F1 : 상세코드 관리 ] 화면에서 [ 삭제 ]기능에서 사용함.  */
							( #{srchCndtCtn} = '2' ) and ( COMN_DTLS_CD = #{comnDtlsCd} ) 
						) 
						or
	   					( 
							/* srchCndtCtn : 3 : [ CCC002F3 : 상세코드 등록 ] 화면에서 [ 행삭제 ]기능에서 사용함. */
							( #{srchCndtCtn} = '3' ) and ( COMN_DTLS_CD = #{comnDtlsCd} ) and ( LNGG_CD = #{lnggCd} ) 
						)
					)

mapper.select:
/*SQL_ID: com-sds-healthcare-ehr-com-cc-ccc-dao-CccComcodeDAO-pagingComCodeList */
/*+ ccd_comcode_l20$S01_코드상세 조회 */ SELECT RNUM , COMN_GRP_CD , COMN_DTLS_CD , DETL_CD_NM , DETL_CD_DSCR_CTN , DETL_CD_NM_1 FROM ( SELECT ROWNUM AS RNUM , C.* FROM (select A.COMN_GRP_CD , A.COMN_DTLS_CD as COMN_DTLS_CD /* 공통세부코드 */ , nvl(B.DETL_CD_NM, A.DETL_CD_NM) as DETL_CD_NM /* 상세코드명 */ , a.DETL_CD_DSCR_CTN as DETL_CD_DSCR_CTN , A.COMN_DTLS_CD || ' - ' || nvl(B.DETL_CD_NM, A.DETL_CD_NM) as DETL_CD_NM_1 from CCCMCDDMT A /* 코드상세 */ , CCCMCDDLT B /* 언어별상세코드명 */ where A.COMN_GRP_CD = B.COMN_GRP_CD(+) and A.COMN_DTLS_CD = B.COMN_DTLS_CD(+) and A.APST_YMD <= sysdate and A.APFN_YMD >= sysdate and B.LNGG_CD(+) = nvl('KOKR', 'KOKR') order by A.COMN_GRP_CD, A.COMN_DTLS_CD)C)
WHERE RNUM > cast(coalesce(#{nextKeyValue}, '0') as number) AND RNUM <= cast(coalesce(#{nextKeyValue}, '0') as number)+#{pageSize}
