select  /*SQL_ID: com-sds-healthcare-ehr-com-cs-ccs-dao-CcsMenuGrpMngmDAO-selectOneMngrHmbyMenuGrp */
			a.HOME_CNT as USE_CQY
		,	b.WORK_CNT as USE_CQY_1
from     ( 	select	count(*) as HOME_CNT
			  	from		CSMNHOMMT
			  	where	MENU_GRP_ID     = #{menuGrpId}
				and    	((nvl(#{menuId}, '*') = '*') or (MENU_ID = #{menuId}))
			) a
		,	( 	select	count(*) as WORK_CNT
				from		CSWKSITMT
				where	MENU_GRP_ID     = #{menuGrpId}
				and		((nvl(#{menuId}, '*') = '*') or (MENU_ID = #{menuId}))
			) b

