/*SQL_ID: com-sds-healthcare-ehr-com-cu-ccu-dao-CcuBoardMngmDAO-selectOneBoardBultClsfAtchFlImgDtl */
SELECT A.BULT_CLSF_ID AS BULT_CLSF_ID /* 게시분류ID */ , A.HGRN_BULT_CLSF_ID AS HGRN_BULT_CLSF_ID /* 상위게시분류ID */ , A.BULT_CLSF_NM AS BULT_CLSF_NM /* 게시분류명 */ , A.BULT_CLSF_DVSN_VL AS BULT_CLSF_DVSN_VL /* 게시분류구분값 */ , A.SMCL_TYP_VL AS SMCL_TYP_VL /* 소분류타입값 */ , A.BULT_SCRN_ID AS BULT_SCRN_ID /* 게시화면ID */ , A.BULT_IMAG_NM AS BULT_IMAG_NM /* 게시이미지명 */ , A.BULT_URL_NM AS BULT_URL_NM /* 게시URL명 */ , A.BULT_SRVC_ID AS BULT_SRVC_ID /* 게시서비스ID */ , B.BULT_ID AS BULT_ID /* 첨부파일게시ID */ , B.ATFL_SNO AS ATFL_SNO /* 첨부파일일련번호 */ , B.ATFL_TYP_CD AS ATFL_TYP_CD /* 첨부유형코드 */ , B.FILE_ID AS FILE_ID /* 파일ID */ , B.FILE_NM AS FILE_NM /* 파일명 */ , B.ATCH_FLDV_VL AS ATCH_FLDV_VL /* 첨부파일구분값 */ FROM CUBLBRCMT A /* 게시판분류관리_T */ , CUBLBRFMT B /* 게시판첨부파일_T */ WHERE A.DLTN_YN = 'N'
AND A.BULT_CLSF_ID = #{bultClsfId} AND A.BULT_CLSF_ID = B.BULT_ID(+) AND A.BULT_IMAG_NM = B.FILE_ID(+)
AND A.HGRN_BULT_CLSF_ID = #{hgrnBultClsfId}
AND A.BULT_CLSF_DVSN_VL = #{bultClsfDvsnVl}
AND A.SMCL_TYP_VL = #{smclTypVl}
ORDER BY A.HGRN_BULT_CLSF_ID, A.BULT_CLSF_ID

/*SQL_ID: com-sds-healthcare-ehr-com-cu-ccu-dao-CcuBoardMngmDAO-selectOneBoardClsfScrnDsplSqnc */

	SELECT (NVL(MAX(CMT.SCRN_DSPL_SQNC), 0) +1) AS SCRN_DSPL_SQNC
	  FROM CUBLBRCMT CMT /* 게시판분류관리_T */
	 WHERE 1=1 




			AND CMT.BULT_CLSF_DVSN_VL = #{bultClsfDvsnVl}




			AND CMT.BULT_CLSF_DVSN_VL = #{bultClsfDvsnVl}
			AND CMT.HGRN_BULT_CLSF_ID = #{hgrnBultClsfId} 




			AND CMT.BULT_CLSF_DVSN_VL = #{bultClsfDvsnVl}
			AND CMT.HGRN_BULT_CLSF_ID = #{hgrnBultClsfId}
