mapper.update:
update  /*SQL_ID: com-sds-healthcare-ehr-med-md-mdp-dao-MdpOsetDetlDAO-updateAcdgAddtCtn */
        MDOSETDTT
   set
        ACDG_ADDT_CTN          = #{acdgAddtCtn}       /* 항암제투여일내용 */

     ,  DDCN                   = case when (ORDR_CLTY_CD = 'B2') and
                                           (#{ddcn} <> 0) then #{ddcn}
                                                          else DDCN
                                 end


     ,  ANTC_ORDV_VL           = #{antcOrdvVl}

     ,  LAST_UPDR_ID           = #{gvUserId}
     ,  LAST_UPDT_IP           = #{gvUserIp}
     ,  LAST_UPDT_DT           = sysdate
     ,  LAST_UPDT_CLNT_PRGM_ID = #{gvClntPrgmId}
     ,  LAST_UPDT_SRVR_PRGM_ID = #{gvSrvrPrgmId}
 where
        OSET_CD            = #{osetCd}
   and  OSET_SNO           = #{osetSno}
   and  OSET_DVSN_CD       = #{osetDvsnCd}
   and  OSET_DETL_SNO      = #{osetDetlSno}

update  /*SQL_ID: com-sds-healthcare-ehr-med-md-mdp-dao-MdpOsetDetlDAO-updateAntcOrdrDetl */
        MDOSETDTT
   set
        ANTC_ORDV_VL           = #{antcOrdvVl}          /* 항암처방구분값 */
     ,  ODKI_CD                = #{odkiCd}              /* 처방종류코드 */
     ,  BDWG_PER_ORDR_VLM      = #{bdwgPerOrdrVlm}      /* 체중당처방용량 */
     ,  BASE_VLM_CLCL_TYPE_CD  = #{baseVlmClclTypeCd}   /* 기준용량계산유형코드 */
     ,  ACDG_CLCL_VL           = #{acdgClclVl}          /* 항암제계산값 */
     ,  ICUN_ADMN_VLM          = #{icunAdmnVlm}         /* 함량단위투여용량 */
     ,  DDCN                   = #{ddcn}                /* 일수 */
     ,  ACDG_ADDT_CTN          = #{acdgAddtCtn}         /* 항암제 투여일 */
     ,  LAST_UPDR_ID           = #{gvUserId}
     ,  LAST_UPDT_IP           = #{gvUserIp}
     ,  LAST_UPDT_DT           = sysdate
     ,  LAST_UPDT_CLNT_PRGM_ID = #{gvClntPrgmId}
     ,  LAST_UPDT_SRVR_PRGM_ID = #{gvSrvrPrgmId}
 where
        OSET_CD       = #{osetCd}
   and  OSET_SNO      = #{osetSno}
   and  OSET_DVSN_CD  = #{osetDvsnCd}
   and  OSET_DETL_SNO = #{osetDetlSno}

mapper.delete:
delete  /*SQL_ID: com-sds-healthcare-ehr-med-md-mdp-dao-MdpOsetDetlDAO-deleteOsetDetl */
  from  MDOSETDTT
 where
        OSET_CD      = #{osetCd}
   and  OSET_DVSN_CD = #{osetDvsnCd}
   and  OSET_SNO in
        (
        select  OSET_SNO
          from  (
                select  OSET_SNO
                     ,  HGRN_OSET_SNO
                  from  MDOSETBST
                 where  OSET_CD      = #{osetCd}
                   and  OSET_DVSN_CD = #{osetDvsnCd}
                ) x
         start
          with  OSET_SNO = #{osetSno}
       connect
            by  PRIOR OSET_SNO = HGRN_OSET_SNO
        )

delete  /*SQL_ID: com-sds-healthcare-ehr-med-md-mdp-dao-MdpOsetDetlDAO-deleteOsetDetlByPK */
  from  MDOSETDTT
 where  OSET_CD         = #{osetCd}              /* SET 모음코드 */
   and  OSET_SNO        = #{osetSno}             /* SET 모음순번 */
   and  OSET_DVSN_CD    = #{osetDvsnCd}

   and  OSET_DETL_SNO = #{osetDetlSno}

delete  /*SQL_ID: com-sds-healthcare-ehr-med-md-mdp-dao-MdpOsetDetlDAO-deleteOsetDetlAll */
  from  MDOSETDTT
 where
        OSET_CD      = #{osetCd}
   and  OSET_DVSN_CD = #{osetDvsnCd}

/*SQL_ID: com-sds-healthcare-ehr-med-md-mdp-dao-MdpOsetDetlDAO-deleteOsetDetlHx */
delete
        MDOSETDHT
 where
        OSET_CD      = #{osetCd}
   and  OSET_DVSN_CD = #{osetDvsnCd}
   and  OSET_SNO in
        (
        select  OSET_SNO
          from  (
                select  OSET_SNO
                     ,  HGRN_OSET_SNO
                  from  MDOSETBST
                 where  OSET_CD      = #{osetCd}
                   and  OSET_DVSN_CD = #{osetDvsnCd}
                ) x
         start
          with  OSET_SNO = #{osetSno}
         connect
            by  prior OSET_SNO = HGRN_OSET_SNO
        )

delete  /*SQL_ID: com-sds-healthcare-ehr-med-md-mdp-dao-MdpOsetDetlDAO-deleteOsetDetlHxAll */
  from  MDOSETDHT
 where
        OSET_CD      = #{osetCd}
   and  OSET_DVSN_CD = #{osetDvsnCd}

mapper.select:
select  /*SQL_ID: com-sds-healthcare-ehr-med-md-mdp-dao-MdpOsetDetlDAO-selectOneMaxOsetDetlSno */
        nvl(max(OSET_DETL_SNO), 0) as OSET_DETL_SNO /* SET처방상세순번 채번 */
  from
        MDOSETDTT
 where  OSET_CD         = #{osetCd}
   and  OSET_SNO        = #{osetSno}
   and  OSET_DVSN_CD    = #{osetDvsnCd}

   and  OSET_DETL_SNO   < 900
