   and  PTNO = #{ptno}

mapper.select:
/*SQL_ID: com-sds-healthcare-ehr-sup-sp-spp-dao-SppComnPthlMstrMngmDAO-selectOnePthlNoNornBefInqr */
    select  /*+ spd_gpthlno_s01$S01_병리번호채번전 조회 */
            to_char(NVL(sn.SNO, 0)+1) PTHL_NO
         , #{spcmNo} || substr(PTEX_NORN_YY, 3,2) || lpad(to_char(NVL(sn.SNO, 0)+1), 5, '0') CD_VL
      from SPASEQNOT sn
     where  PTEX_NORN_LRCL_CD = #{spcmNo}
       and  PTEX_NORN_YY = (select to_char(sysdate, 'yyyy')
                              from dual)
       for update of sn.sno

