mapper.delete:
/*SQL_ID: com-sds-healthcare-ehr-sup-sr-srp-dao-SrpComnPtntExmnDAO-deleteSwworkmmt */
DELETE  FROM SWWORKMMT
	 where  PTNO    = #{ptno}
	   and  ORDR_YMD  = to_date(#{ordrYmd}, 'yyyymmdd')
	   and  ORDR_SNO = #{ordrSno}

/*SQL_ID: com-sds-healthcare-ehr-sup-sr-srp-dao-SrpComnPtntExmnDAO-deleteSvworkdmt */
delete  SVWORKDMT
where   PTNO    = #{ptno}
    and  ORDR_YMD  = to_date(#{ordrYmd}, 'yyyymmdd')
	and  ORDR_SNO = #{ordrSno}

mapper.select:
/*SQL_ID: com-sds-healthcare-ehr-sup-sr-srp-dao-SrpComnPtntExmnDAO-selectOneSwordrcmt */
   select  decode(EXCF_CD,'SV','',APNT_SCHD_SBTY_NM) as SCHD_DTLS_TYP_CD /* 스케줄세부타입코드 */
           ,  THDM_IMPL_YN as EXMN_D3_RQST_YN /* 검사3D요청여부     */
           ,  EXRM_EXCF_CD as EXRM_EXCF_CD     /* 핵의학검사분류코드 */ 
     from  SWORDRCMT
   where  ORDR_CD = #{ordrCd}

/*SQL_ID: com-sds-healthcare-ehr-sup-sr-srp-dao-SrpComnPtntExmnDAO-selectOneSvworkdmt */
 select  PTNO
  from   SVWORKDMT
where   PTNO    = #{ptno}
    and  ORDR_YMD  = to_date(#{ordrYmd}, 'yyyymmdd')
	and  ORDR_SNO = #{ordrSno}
